import React, { useState } from 'react';

const ExaDeMedicalViewPage = () => {
  const [data, setData] = useState([
    { date: '2023-07-01', stuID: '001', pdf: 'file1.pdf', reason: 'Document 1', status: 'Pending', description: 'Description 1' },
    { date: '2023-07-02', stuID: '002', pdf: 'file2.pdf', reason: 'Document 2', status: 'Approved', description: 'Description 2' },
    { date: '2023-07-03', stuID: '003', pdf: 'file3.pdf', reason: 'Document 3', status: 'Rejected', description: 'Description 3' },
  ]);

  const handleStatusChange = (index, event) => {
    const updatedData = [...data];
    updatedData[index].status = event.target.value;
    setData(updatedData);
  };

  const tableRows = data.map((row, index) => (
    <tr key={index}>
      <td>{row.date}</td>
      <td>{row.stuID}</td>
      <td>{row.pdf}</td>
      <td>{row.reason}</td>
      <td>
        <select value={row.status} onChange={(event) => handleStatusChange(index, event)}>
          <option value="Pending">Pending</option>
          <option value="Approved">Approved</option>
          <option value="Rejected">Rejected</option>
        </select>
      </td>
      <td>{row.description}</td>
    </tr>
  ));

  return (
    <div>
      <table>
        <thead>
          <tr>
            <th>Date</th>
            <th>StuID</th>
            <th>PDF</th>
            <th>Reason</th>
            <th>Action</th>
            <th>Description</th>
          </tr>
        </thead>
        <tbody>
          {tableRows}
        </tbody>
      </table>
    </div>
  );
};

export default ExaDeMedicalViewPage;
