import React, { useState } from 'react';

const StuMedicalUplaodPage = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [reason, setReason] = useState('');
  const [file, setFile] = useState(null);
  const [data, setData] = useState([
    { date: '2023-07-01', pdf: 'file1.pdf', reason: 'Document 1', status: 'Approved' },
    { date: '2023-07-02', pdf: 'file2.pdf', reason: 'Document 2', status: 'Pending' },
    { date: '2023-07-03', pdf: 'file3.pdf', reason: 'Document 3', status: 'Rejected' },
  ]);

  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
  };

  const handleReasonChange = (event) => {
    setReason(event.target.value);
  };

  const handleFileChange = (event) => {
    const uploadedFile = event.target.files[0];
    setFile(uploadedFile);
  };

  const handleAdd = () => {
    const newEntry = {
      date: '',
      pdf: file ? file.name : '',
      reason: reason,
      status: 'Pending',
    };
    setData([...data, newEntry]);
    setReason('');
    setFile(null);
  };

  const filteredData = data.filter((row) => {
    if (row.status.toLowerCase().includes(searchQuery.toLowerCase())) {
      return row;
    }
    return false;
  });

  const tableRows = filteredData.map((row, index) => (
    <tr key={index}>
      <td>{row.date}</td>
      <td>{row.pdf}</td>
      <td>{row.reason}</td>
      <td>{row.status}</td>
    </tr>
  ));

  return (
    <div>
      <div>
        <input type="text" value={searchQuery} onChange={handleSearchChange} placeholder="Search..." />
      </div>
      <table>
        <thead>
          <tr>
            <th>Date</th>
            <th>PDF</th>
            <th>Reason</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {tableRows}
        </tbody>
      </table>
      <div>
        <textarea value={reason} onChange={handleReasonChange} placeholder="Reason"></textarea>
        <input type="file" accept="application/pdf" onChange={handleFileChange} />
        <button onClick={handleAdd}>Add</button>
      </div>
    </div>
  );
};

export default StuMedicalUplaodPage;
