import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function MarksAdd() {
    const navigate = useNavigate();

    const [inputs, setInputs] = useState({
        studentId: "",
        subjectId: "",
        studentName: "",
        mark: "",
        grade: "",
    });

    const handleChange = (event) => {
        const { name, value } = event.target;
        setInputs((prevInputs) => ({
            ...prevInputs,
            [name]: value
        }));
    };

    const handleSubmit = (event) => {
        event.preventDefault();

        axios
            .post("http://localhost:80/api - Original - Copy/marks/save.php", inputs)
            .then(function (response) {
                console.log(response.data);
                navigate("/");
            })
            .catch(function (error) {
                console.error(error);
            });
    };

    return (
        <div>
            <h1>Add Mark</h1>
            <form onSubmit={handleSubmit}>
                <table cellSpacing="10">
                    <tbody>
                        <tr>
                            <th>
                                <label>Student ID: </label>
                            </th>
                            <td>
                                <input
                                    type="text"
                                    name="studentId"
                                    value={inputs.studentId}
                                    onChange={handleChange}
                                />
                            </td>
                        </tr>
                        <tr>
                            <th>
                                <label>Email: </label>
                            </th>
                            <td>
                                <input
                                    type="text"
                                    name="email"
                                    value={inputs.email}
                                    onChange={handleChange}
                                />
                            </td>
                        </tr>
                        <tr>
                            <th>
                                <label>Mobile: </label>
                            </th>
                            <td>
                                <input
                                    type="text"
                                    name="mobile"
                                    value={inputs.mobile}
                                    onChange={handleChange}
                                />
                            </td>
                        </tr>
                        <tr>
                            <th>
                                <label>Mobile: </label>
                            </th>
                            <td>
                                <input
                                    type="text"
                                    name="mobile"
                                    value={inputs.mobile}
                                    onChange={handleChange}
                                />
                            </td>
                        </tr>
                        <tr>
                            <th>
                                <label>Mobile: </label>
                            </th>
                            <td>
                                <input
                                    type="text"
                                    name="mobile"
                                    value={inputs.mobile}
                                    onChange={handleChange}
                                />
                            </td>
                        </tr>
                        <tr>
                            <td colSpan="2" align="right">
                                <button type="submit">Save</button>
                            </td>
                        </tr>
                    </tbody>
                </table>
            </form>
        </div>
    );
}
