import { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";

export default function EditUser() {
  const navigate = useNavigate();

  const [inputs, setInputs] = useState({
      name: "",
      email: "",
      mobile: "",
  });

  const { id } = useParams();

  useEffect(() => {
      getUser();
  }, []);

  async function getUser() {
      try {

          const response = await axios.get(`http://localhost:80/api/user/${'_id'}`);
          console.log(response.data);
          setInputs(response.data);
      } catch (error) {
          console.error("Error fetching user data:", error);
      }
  }

  const handleChange = (event) => {
      const { name, value } = event.target;
      setInputs((values) => ({ ...values, [name]: value }));
  };

  const handleSubmit = (event) => {
      event.preventDefault();

      axios.put(`http://localhost:80/api/user/${'_id'}/edit`, inputs).then(function(response) {
          console.log(response.data);
          navigate("/");
      });
  };

    return (
        <div>
            <h1>Edit user</h1>
            <form onSubmit={handleSubmit}>
                <table cellSpacing="10">
                    <tbody>
                        <tr>
                            <th>
                                <label>Name: </label>
                            </th>
                            <td>
                                <input value={inputs.name} type="text" name="name" onChange={handleChange} />
                            </td>
                        </tr>
                        <tr>
                            <th>
                                <label>Email: </label>
                            </th>
                            <td> 
                                <input value={inputs.email} type="text" name="email" onChange={handleChange} />
                            </td>
                        </tr>
                        <tr>
                            <th>
                                <label>Mobile: </label>
                            </th>
                            <td>
                                <input value={inputs.mobile} type="text" name="mobile" onChange={handleChange} />
                            </td>
                        </tr>
                        <tr>
                            <td colSpan="2" align="right">
                                <button>Save</button>
                            </td>
                        </tr>
                    </tbody>
                </table>
            </form>
        </div>
    );
}
