import React, { useState } from 'react';

const Dropdown = () => {
  const [selectedOption, setSelectedOption] = useState('');

  const handleSelectChange = (event) => {
    setSelectedOption(event.target.value);
  };

  return (
    <div>
      <h3>Selected Option: {selectedOption}</h3>
      <select value={selectedOption} onChange={handleSelectChange}>
        <option value="">Select an option</option>
        <option value="option1">Option 1</option>
        <option value="option2">Option 2</option>
        <option value="option3">Option 3</option>
      </select>
    </div>
  );
};

const Table = () => {
  const [data, setData] = useState([
    { stuID: 1, subName: 'Math', subID: 'MATH101', marks: 90 },
    { stuID: 2, subName: 'Science', subID: 'SCI101', marks: 85 },
    { stuID: 3, subName: 'English', subID: 'ENG101', marks: 95 },
  ]);

  return (
    <div>
      <table>
        <thead>
          <tr>
            <th>Stu ID</th>
            <th>Sub Name</th>
            <th>Sub ID</th>
            <th>Marks</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {data.map((row, index) => (
            <tr key={index}>
              <td>{row.stuID}</td>
              <td>{row.subName}</td>
              <td>{row.subID}</td>
              <td>{row.marks}</td>
              <td>
                <button>Edit</button>
                <button>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

const AppOne = () => {
  return (
    <div>
      <h1>React Dropdown and Table Example</h1>
      <Dropdown />
      <Table />
    </div>
  );
};

export default AppOne;
