import { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

export default function MarkPage() {
    const [users, setUsers] = useState([]);

    useEffect(() => {
        getUsers();
    }, []);

    function getUsers() {
        axios.get('http://localhost:80/api/users/').then(function(response) {
            console.log(response.data);
            setUsers(response.data);
            
        });
    }

    const deleteUser = (id) => {
        axios.delete(`http://localhost:80/api/user/${id}`).then(function(response){
            console.log(response.data);
            getUsers();
            
        });
    }

    return (
        <div>
            <Link to={"MarksAddPage"}><button>Insert</button></Link>
            <table>
                <thead>
                    <tr>
                        <th>#</th>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Mobile</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map((user, key) => (
                        <tr key={key}>
                            <td>{user._id.$oid}</td>
                            <td>{user.name}</td>
                            <td>{user.email}</td>
                            <td>{user.mobile}</td>
                            <td>
                            <Link to={`user/${user._id.$oid}/MarkEditPage`} style={{marginRight: "10px"}}>Edit</Link>
                                <button onClick={() => deleteUser(user._id.$oid)}>Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    )
}
