import React, { useState } from 'react';

const StuMedicalView = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [data, setData] = useState([
    { date: '2023-07-01', pdf: 'file1.pdf', reason: 'Document 1', status: 'Approved' },
    { date: '2023-07-02', pdf: 'file2.pdf', reason: 'Document 2', status: 'Pending' },
    { date: '2023-07-03', pdf: 'file3.pdf', reason: 'Document 3', status: 'Rejected' },
  ]);

  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
  };

  const handleStatusChange = (index, event) => {
    const updatedData = [...data];
    updatedData[index].status = event.target.value;
    setData(updatedData);
  };

  const handleUpload = (event) => {
    const file = event.target.files[0];
    // Perform upload logic here
    console.log('File uploaded:', file.name);
  };

  const filteredData = data.filter((row) => {
    if (row.status.toLowerCase().includes(searchQuery.toLowerCase())) {
      return row;
    }
    return false;
  });

  const tableRows = filteredData.map((row, index) => (
    <tr key={index}>
      <td>{row.date}</td>
      <td>{row.pdf}</td>
      <td>{row.reason}</td>
      <td>
        <select value={row.status} onChange={(event) => handleStatusChange(index, event)}>
          <option value="Pending">Pending</option>
          <option value="Approved">Approved</option>
          <option value="Rejected">Rejected</option>
        </select>
      </td>
    </tr>
  ));

  return (
    <div>
      <div>
        <input type="text" value={searchQuery} onChange={handleSearchChange} placeholder="Search..." />
        <input type="file" accept="application/pdf" onChange={handleUpload} />
      </div>
      <table>
        <thead>
          <tr>
            <th>Date</th>
            <th>PDF</th>
            <th>Reason</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {tableRows}
        </tbody>
      </table>
    </div>
  );
};

export default StuMedicalView;
