import React, { useState } from 'react';

const ExaDeMedicalViewPage = () => {
  const [data, setData] = useState([
    { date: '2023-07-01', stuID: '001', pdf: 'file1.pdf', reason: 'Document 1', status: 'Pending' },
    { date: '2023-07-02', stuID: '002', pdf: 'file2.pdf', reason: 'Document 2', status: 'Approved' },
    { date: '2023-07-03', stuID: '003', pdf: 'file3.pdf', reason: 'Document 3', status: 'Rejected' },
  ]);

  const [description, setDescription] = useState('');

  const handleStatusChange = (index, event) => {
    const updatedData = [...data];
    updatedData[index].status = event.target.value;
    setData(updatedData);
  };

  const handleDescriptionChange = (event) => {
    setDescription(event.target.value);
  };

  const tableRows = data.map((row, index) => (
    <tr key={index}>
      <td>{row.date}</td>
      <td>{row.stuID}</td>
      <td>{row.pdf}</td>
      <td>{row.reason}</td>
      <td>
        <select value={row.status} onChange={(event) => handleStatusChange(index, event)}>
          <option value="Pending">Pending</option>
          <option value="Approved">Approved</option>
          <option value="Rejected">Rejected</option>
        </select>
      </td>
      <td>{description}</td>
    </tr>
  ));

  return (
    <div>
      <table>
        <thead>
          <tr>
            <th>Date</th>
            <th>StuID</th>
            <th>PDF</th>
            <th>Reason</th>
            <th>Action</th>
            <th>Description</th>
          </tr>
        </thead>
        <tbody>
          {tableRows}
        </tbody>
      </table>

      <div>
        <label>
          Description:
          <input type="text" value={description} onChange={handleDescriptionChange} />
        </label>
      </div>
    </div>
  );
};

export default ExaDeMedicalViewPage;
